import {
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Icon,
  Typography,
} from "@mui/material";
import React from "react";
import { ReactComponent as TelevisionIcon } from "../assets/images/television.svg";

export const CustomCard = (props) => {
  const { Info, customStyles, ...other } = props;
  const { title, Description, image } = Info; // Destructure Info to get the properties

  return (
    <>
      <Card
        // sx={{
        //   borderRadius: "1rem",
        //   background: "linear-gradient(149deg, #192247 0%, #210e17 96.86%)",
        //   width: "400px",
        //   height: "300px",
        // }}
      >
        <CardContent>
          <Typography
            mt={"5px"}
            fontWeight={500}
            fontSize={"1.5rem"}
            color="white"
          >
            {title}
          </Typography>
          <Typography
            mt={"5px"}
            color="rgba(255, 255, 255, 0.7)"
            fontSize={"1rem"}
            fontWeight={400}
          >
            {Description}
          </Typography>
        </CardContent>
        <CardMedia>{image}</CardMedia>
      </Card>
    </>
  );
};

export default CustomCard;
