import { createTheme } from "@mui/material";


const theme = createTheme({
  palette: {
    primary: {
      main: "#e50914",
    },
    secondary: {
      main: "rgba(0, 0, 0, 0.50)",
    },
    warning: {
      light: "rgba(253, 126, 20, 0.18)",
      main: "rgba(253, 126, 20, 1)",
    },
  },
  components: {
    MuiFormLabel: {
      styleOverrides: {
        root: {
          background: "transparent", // Set background to transparent or desired color
        },
      },
    },
    MuiOutlinedInput: {
      styleOverrides: {
        root: {
          background: "white", // Set background color for input fields
          borderRadius: "8px",
          borderColor: "#aaaaaa",
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          background: "rgb(255,0,0)", // Set background color for buttons
          padding: "12px 16px",
          fontWeight: 600,
          fontSize: "14px",
          lineHeight: 1,
          borderWidth: "2px",
          "&:hover": {
            background: "#005bb5", // Darker background on hover
          },
        },
      },
    },
    MuiTableCell: {
      styleOverrides: {
        root: {
          background: "#f5f5f5", // Set background color for table cells
        },
      },
    },
  },
});

export default theme;
