import React from "react";
import CustomButton from "./button";
import { AddCircle, Description } from "@mui/icons-material";
import { Grid2 } from "@mui/material";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import { ReactComponent as TelevisionIcon } from "../assets/images/television.svg";
import { ReactComponent as ArrowIcon } from "../assets/images/arrow.svg";
import { ReactComponent as TorchIcon } from "../assets/images/torch.svg";
import { ReactComponent as SmileyIcon } from "../assets/images/smiley.svg";
import CustomCard from "./card";
export const Render = () => {
  const info = [
    {
      title: "Enjoy on your TV",
      Description:
        "Watch on smart TVs, PlayStation, Xbox, Chromecast, Apple TV, Blu-ray players and more.",
      image: <TelevisionIcon />,
    },

    {
      title: "Download your shows to watch offline",
      Description:
        "Save your favourites easily and always have something to watch.",
      image: <ArrowIcon />,
    },

    {
      title: "Watch everywhere",
      Description:
        "Stream unlimited movies and TV shows on your phone, tablet, laptop and TV.",
      image: <TorchIcon />,
    },
    {
      title: "Create profiles for kids",
      Description:
        "Send kids on adventures with their favourite characters in a space made just for them — free with your membership.",
      image: <SmileyIcon />,
    },
  ];

  const customStyles = [
    {
      card: {
        borderRadius: "1rem",
        background: "linear-gradient(149deg, #192247 0%, #210e17 96.86%)",
        width: "400px",
        height: "300px",
      },
      imageContainer: {},
      paragraph: {
        color: "rgba(255, 255, 255, 0.7)",
        fontSize: "1rem",
        fontWeight: 400,
      },

      typography: {
        fontWeight: 500,
        fontSize: "1.5rem",
        color: "white",
      },
      image
    },
  ];
  return (
    <>
      <Grid2 spacing={3} container>
        <Grid2 item>
          <CustomButton
            colorType="primary"
            onClick={() => console.log("Button clicked!")}
          >
            SignIn
          </CustomButton>
        </Grid2>
        <Grid2 item>
          <CustomButton
            customstyles={{
              "&:hover": {
                background: "#e50914",
              },
            }}
            colorType="secondary"
            endIcon={<ArrowForwardIosIcon />}
            onClick={() => console.log("Button clicked!")}
          >
            Get Started
          </CustomButton>
        </Grid2>
        <Grid2 item>
          <CustomButton onClick={() => console.log("Button clicked!")}>
            Use a sign-in code
          </CustomButton>
        </Grid2>
        <Grid2 item container spacing={2}>
          {info.map((item, index) => (
            <Grid2 item key={index}>
              <CustomCard styles = {customStyles[0]} Info={item} />
            </Grid2>
          ))}
        </Grid2>
      </Grid2>
    </>
  );
};
