import { createTheme } from "@mui/material";

const theme = createTheme({
  palette: {
    primary: {
      main: "#e50914",
    },
    secondary: {
      main: "rgba(128, 0, 0, 0.50)",
    },
    warning: {
      light: "rgba(253, 126, 20, 0.18)",
      main: "rgba(253, 126, 20, 1)",
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          color: "#ffffff",
          background: "#e50914",
          lineHeight: "24px",
          letterSpacing:"normal",
          fontWeight: 500,
          fontFamily: "Netflix Sans",
          fontSize: "16px",  
          borderRadius: "4px",
          "&:hover": {
            background: "#e50914",
          },
        },
      },
    },
  },
});

export default theme;
